var texto = document.getElementById("message").value;
var enviar = document.getElementById("enviar").value.replace(/\D/g,'');


//Comprobar que tiene algo escrito
if(texto.trim().length && enviar.trim().length){

    //Iniciar RC SDK con credenciales
    var rcsdk = new RingCentral.SDK({
        'server': 'https://platform.ringcentral.com', 
        'clientId': '9HbuQrJrz91dX2plLImQtu', 
        'clientSecret': 'WOx7xpSAb4hafcnExdBXPb7jJsAMwFIldfdG0Kuy3PxK',
        'redirectUri': 'https://francistax--skuid.vf.force.com/apex/skuid__ui?page=CallLog'
    });

    //Llamada a API para enviar mensaje
    rcsdk.post('/restapi/v1.0/account/~/extension/~/sms', {
        from: {phoneNumber: '16124293055'},
        to: [
            {phoneNumber: enviar},
        ],
        text: texto,
    })
    .then(function (response) {
        return response.json();
    })
    .then(function (json) {
        alert('Success: ' + json.id);
    })
    .catch(function (e) {
        alert('Error: ' + e.message);
        if(e.message == "Refresh token has expired" || e.message == "Refresh token is missing" ||
            e.message == "OAuth token is invalid" || e.message == "Token not found"){
