            var loginOptions = rcsdk.parseLoginRedirect(window.location.hash || window.location.search);
            rcsdk.login(loginOptions).then(function (result){
                window.location.href = "https://francistax--skuid.vf.force.com/apex/skuid__ui?page=CallLog";
            });
        }
    });
}

//Iniciar RC SDK con credenciales
var rcsdk = new RingCentral.SDK({
    'server': 'https://platform.ringcentral.com', 
    'clientId': '9HbuQrJrz91dX2plLImQtu', 
    'clientSecret': 'WOx7xpSAb4hafcnExdBXPb7jJsAMwFIldfdG0Kuy3PxK',
    'redirectUri': 'https://francistax--skuid.vf.force.com/apex/skuid__ui?page=CallLog'
});

//Obtener fecha de mensajes de hace 1 mes desde la fecha actual
var fecha = new Date();
fecha.setMonth(fecha.getMonth() - 8);
fecha.setHours(0, 0, 0, 0);
var isoFormatDate = fecha.toISOString();

//Función para iniciar sesión
async function asyncCall() {

    rcsdk.get(`https://platform.ringcentral.com/restapi/v1.0/account/~/extension/~/message-store?dateFrom=${isoFormatDate}`, {})
    .then(function (response) {
        return response.json();
    })
    .then(function (response) {
        var sms = response.records.filter(obj => {
            return obj.type === "SMS"
        });
        var result = sms.reduce(function (r, a) {
          r[a.conversationId] = r[a.conversationId] || [];
          r[a.conversationId].push(a);
          return r;
        }, Object.create(null));
        var mensajes = "";
        for (let key in result) {
          if(key !== "undefined"){
            console.log(result[key][0]);
            var conversationId = result[key][0].conversation.id;
            var subject = result[key][0].subject;
            var from;
            if(result[key][0].direction == "Outbound"){
                (result[key][0].to[0].name) ? from = result[key][0].to[0].name : from = result[key][0].to[0].phoneNumber;
                if(result[key][0].to[0].phoneNumber){
                    var phoneNumber = result[key][0].to[0].phoneNumber;
                }
            }else{
                (result[key][0].from.name) ? from = result[key][0].from.name : from = result[key][0].from.phoneNumber;
                if(result[key][0].from.phoneNumber){
                    var phoneNumber = result[key][0].from.phoneNumber;
                }
            }

            var time = new Date(result[key][0].creationTime).toLocaleString('en-US', { hour: 'numeric', minute: 'numeric', hour12: true });
            var mensaje = "<div style='font-family:Roboto Flex;display: flex;padding:20px 32px 20px 32px;border-bottom:1px solid #edeeef;'>";
            mensaje += "       <div style='width:80%;'>";
            mensaje += "           <b title='"+phoneNumber+"' class='mensajes' id='"+conversationId+"' style='cursor:pointer;color: #1f2532;'>"+from+"</b><br>";
            if(result[key][0].direction == "Outbound"){
                mensaje += "       <span style='font-weight:200;color:#a5a8ad;font-size:13px;'>Tú: "+subject+"</span>";
            }else{
                if(result[key][0].readStatus == "Unread"){
                    mensaje += "       <span style='font-weight:600;color:#000;font-size:13px;'>• "+subject+"</span>";
                }else{
                    mensaje += "       <span style='font-weight:200;color:#a5a8ad;font-size:13px;'>"+subject+"</span>";
                }
            }
            mensaje += "       </div>";
            mensaje += "       <div style='width:20%;text-align:right;'><span style='font-size:11px;color:#4c515b;'>"+time+"</span></div>";
            mensaje += "   </div>";
            mensajes += mensaje;
          }
        }
        document.getElementById("smsList").innerHTML = mensajes;
        document.getElementById("chatWrapper").style.display = "none";
    })
    .catch(function (e) {
        console.log(e.message);
        if(e.message == "Refresh token has expired" || e.message == "Refresh token is missing" ||
            e.message == "OAuth token is invalid" || e.message == "Token not found"){
            var loginUrl = rcsdk.loginUrl();
            window.location.assign(loginUrl);

            var loginOptions = rcsdk.parseLoginRedirect(window.location.hash || window.location.search);
            rcsdk.login(loginOptions).then(function (result){
                window.location.href = "https://francistax--skuid.vf.force.com/apex/skuid__ui?page=CallLog";
            });
        }
    });
}
