document.addEventListener('click', function(e) {
    if(e.target.className == "mensajes"){
        document.getElementById("composeGrid").style.display = "none";
        document.getElementById("contactGrid").style.display = "block";
        document.getElementById("chatWrapper").style.display = "block";
        document.getElementById("from_name").innerHTML = "<div style='text-align:center;font-family:Roboto Flex;font-size:20px;font-weight:600;color:#1f2532;'>"+e.target.innerText+"</div>";
        document.getElementById("enviar").value = e.target.title;
        rcsdk.get(`https://platform.ringcentral.com/restapi/v1.0/account/~/extension/~/message-store?dateFrom=${isoFormatDate}&conversationId=${e.target.id}`, {})
        .then(function (response) {
            return response.json();
        })
        .then(function (sms) {
            //Ordenar mensajes de menor a mayor
            sms.records.sort().reverse();

            //Contenedor de mensajes HTML
            var messages = "";

            //Entrar a lista de mensajes
            sms.records.forEach(function (mensaje, index) {

                //Hora de cada mensaje
                var time = new Date(mensaje.creationTime).toLocaleString('en-US', { hour: 'numeric', minute: 'numeric', hour12: true });

                //Fecha de mensajes en español
                var spanishDate = new Date(mensaje.creationTime).toLocaleString('es-MX', { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' });
                var messageDates = "<br><div style='display: flex; justify-content: center; color: #6a6a6a; font-size: 13px;'>"+spanishDate+"</div><br><br>";
                var from;
                (mensaje.from.name) ? from = mensaje.from.name : from = mensaje.from.phoneNumber;
                //Si los mensajes son entrantes, mostrarlos del lado izquierdo y con otros estilos
                if(mensaje.direction == "Inbound"){
                    var messageBox = "<div style='display:flex;'><div style='margin-bottom: 20px;'>";
                    messageBox += "     <div style='display:flex;align-items:center;font-family: Roboto Flex; font-size: 13px;margin-left: 5px;'>";
                    messageBox += "         <span style='margin-right: 5px;font-weight: bold;'>"+from+"</span>";
                    messageBox += "         <span style='color: #6a6a6a;'>"+time+"</span>";
                    messageBox += "     </div>";
                    messageBox += "     <div style='padding: 10px 15px 10px 15px; background: #f5f5fa; border-radius: 15px; margin-top: 5px; display: inline-block;'>";
                    messageBox += "         <span style='font-family: Roboto Flex; font-size: 14px;'>"+mensaje.subject+"</span>";
                    messageBox += "     </div>";
                    messageBox += "   </div></div>";

                //Si son salientes, a la derecha en azul
                }else if(mensaje.direction == "Outbound"){
                    var messageBox = "<div style='display:flex;justify-content: end;'>";
                    messageBox += "     <div style='margin-bottom: 20px;'>";
                    messageBox += "         <div style='display:flex;align-items:center;font-family: Roboto Flex; font-size: 13px;margin-right: 5px;'>";
                    messageBox += "             <span style='margin-right: 5px; color: #6a6a6a;'>"+time+"</span>";
                    messageBox += "             <span style='font-weight: bold;'>"+from+"</span>";
                    messageBox += "         </div>";
                    messageBox += "         <div style='display: flex;justify-content: end;'>";
                    messageBox += "             <span style='font-family: Roboto Flex; font-size: 14px; padding: 10px 15px 10px 15px; background: #0548d3; border-radius: 15px; margin-top: 5px; display: inline-block; color: #fff;'>"+mensaje.subject+"</span>";
                    messageBox += "         </div>";
                    messageBox += "     </div>";
                    messageBox += "   </div>";
                }

                //Si existe el mensaje anterior
                if(sms.records[index - 1]){

                    //Obtener día, mes y año del mensaje anterior
                    var nextMessageDate = sms.records[index - 1].creationTime.split('T')[0];

                    //Si es diferente la fecha del mensaje actual al anterior
                    if(mensaje.creationTime.split('T')[0] !== nextMessageDate){

                        //Mostrar fecha en español antes del mensaje
                        messages += messageDates + messageBox;

                    }else{
                        //No mostrar fecha en español si es la misma al mensaje anterior
                        messages += messageBox;
                    }

                //Si no hay mensajes anteriores
                }else{

                    //Mostrar fecha en español antes del mensaje
                    messages += messageDates + messageBox;

                }        
            });

            //Agregar contenedor de mensajes en el lienzo con id "ChatBox"
            document.getElementById('chatBox').innerHTML = messages;

            //Establecer scroll de Wrapper hasta el final, mostrando por default los sms más recientes
            var wrapper = document.getElementById("messagesWrapper");
            wrapper.scrollTop = wrapper.scrollHeight;

            //Mostrar en consola respuesta de la API
            console.log(sms);
        })    
        .catch(function (e) {
            console.log(e);
        });
    }
}, false);

