        var records = response.records;
        var div = document.createElement( "div" );
        div.id = "options";
        for(var i = 0; i < records.length; i++){
            var name = records[i].firstName;
            var phone = records[i].companyPhone;
            var option = document.createElement("div"); option.className = "optionsCompose";
            option.innerText = name + ` (${phone})`;
            div.appendChild(option);
        }
        document.getElementById("compose").appendChild(div);
        document.getElementById('chatBox').innerHTML = "";
    })
    .catch(function (e) {
        console.log(e.message);
        if(e.message == "Refresh token has expired" || e.message == "Refresh token is missing" ||
            e.message == "OAuth token is invalid"){
            var loginUrl = rcsdk.loginUrl();
            window.location.assign(loginUrl);

            var loginOptions = rcsdk.parseLoginRedirect(window.location.hash || window.location.search);
            rcsdk.login(loginOptions).then(function (result){
                window.location.href = "https://francistax--skuid.vf.force.com/apex/skuid__ui?page=CallLog";
            });
        }
    });


}
document.getElementById("composeGrid").style.display = "block";
document.getElementById("contactGrid").style.display = "none";
document.getElementById('chatBox').innerHTML = "";
document.getElementById("chatWrapper").style.display = "block";
document.getElementById("enviar").value = "";
document.getElementById("enviar").focus();
